import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment.prod';
import { User } from '../interfaces/UsersInterfaces';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import Swal from 'sweetalert2';

@Injectable({
  providedIn: 'root',
})
export class UsersListService {
  private path = environment.api;
  private headers = new HttpHeaders({
    'Content-Type': 'application/json',
  });

  constructor(private http: HttpClient) {}

  getUsersList() {
    const url = this.path + '/Users';
    return this.http.get<User[]>(url);
  }

  setUser(userObject: User) {
    const url = this.path + '/Users';

    this.http
      .post(url, JSON.stringify(userObject), { headers: this.headers })
      .subscribe({
        next: (rta) => console.log('Respuesta del servidor:', rta),
        error: (error) => {
          console.error('Error de validación:', error.error.errors);
          if (error.error.errors) {
            for (const key in error.error.errors) {
              if (error.error.errors.hasOwnProperty(key)) {
                console.log(
                  `Error en el campo ${key}:`,
                  error.error.errors[key]
                );
              }
            }
          }
        },
      });
  }

  updateUser(userObject: User, userId: number) {
    const url = this.path + '/Users/' + userId;

    this.http
      .put(url, JSON.stringify(userObject), { headers: this.headers })
      .subscribe({
        next: (rta) => {
          console.log('Respuesta del servidor:', rta);
          // Añadir confirmación al actualizar el usuario
          Swal.fire({
            title: '¡Usuario actualizado!',
            text: 'La información del usuario ha sido actualizada correctamente.',
            icon: 'success',
          }).then(() => {
            // Aquí puedes recargar la lista de usuarios si es necesario
            this.reloadUsersList();
          });
        },
        error: (error) => {
          console.error('Error de validación:', error.error.errors);
          // Mostrar un mensaje de error si la actualización falla
          Swal.fire({
            title: 'Error',
            text: 'Ocurrió un error al actualizar el usuario.',
            icon: 'error',
          });

          if (error.error.errors) {
            for (const key in error.error.errors) {
              if (error.error.errors.hasOwnProperty(key)) {
                console.log(
                  `Error en el campo ${key}:`,
                  error.error.errors[key]
                );
              }
            }
          }
        },
      });
  }

  reloadUsersList() {
    this.getUsersList().subscribe((usersList) => {
      console.log('Lista actualizada de usuarios:', usersList);
      return usersList;
    });
  }

  deleteUser(userId: number) {
    const url = this.path + '/Users/' + userId;
    return this.http.delete(url, { headers: this.headers });
  }
}
