import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment.prod';
import { User } from '../interfaces/UsersInterfaces';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Injectable({
  providedIn: 'root',
})
export class UsersListService {
  private path = environment.api;

  constructor(private http: HttpClient) {}

  getUsersList() {
    const url = this.path + '/Users';
    return this.http.get<User[]>(url);
  }

  setUser(userObject: User) {
    const url = this.path + '/Users';
    console.log('serviceUser', userObject);

    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
    });

    let myUserJSON = JSON.stringify(userObject);

    this.http
      .post(url, JSON.stringify(userObject), { headers: headers })
      .subscribe({
        next: (rta) => console.log('Respuesta del servidor:', rta),
        error: (error) => {
          console.error('Error de validación:', error.error.errors);
          if (error.error.errors) {
            for (const key in error.error.errors) {
              if (error.error.errors.hasOwnProperty(key)) {
                console.log(
                  `Error en el campo ${key}:`,
                  error.error.errors[key]
                );
              }
            }
          }
        },
      });
  }
}
