import { CommonModule } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  OnInit,
  inject,
} from '@angular/core';
import { MatCardModule } from '@angular/material/card';
import { MatTableModule } from '@angular/material/table';
import { MaterialModule } from 'src/app/material.module';
import { MatIconModule } from '@angular/material/icon';
import { MatMenuModule } from '@angular/material/menu';
import { MatButtonModule } from '@angular/material/button';
import { MatDatepickerModule } from '@angular/material/datepicker';
import {
  FormControl,
  Validators,
  FormsModule,
  ReactiveFormsModule,
  FormGroup,
} from '@angular/forms';
import { MatInputModule } from '@angular/material/input';
import {
  MatError,
  MatFormFieldModule,
  MatLabel,
} from '@angular/material/form-field';
import {
  MatDialog,
  MatDialogActions,
  MatDialogClose,
  MatDialogContent,
  MatDialogTitle,
} from '@angular/material/dialog';
import { provideNativeDateAdapter } from '@angular/material/core';

import { UsersListService } from 'src/app/services/users-list.service';
import { User } from 'src/app/interfaces/UsersInterfaces';

// table 1
export interface productsData {
  id: number;
  imagePath: string;
  uname: string;
  budget: number;
  priority: string;
}

const PRODUCT_DATA: productsData[] = [
  {
    id: 1,
    imagePath: 'assets/images/profile/user-1.jpg',
    uname: 'iPhone 13 pro max-Pacific Blue-128GB storage',
    budget: 180,
    priority: 'confirmed',
  },
  {
    id: 2,
    imagePath: 'assets/images/profile/user-2.jpg',
    uname: 'Apple MacBook Pro 13 inch-M1-8/256GB-space',
    budget: 90,
    priority: 'cancelled',
  },
  {
    id: 3,
    imagePath: 'assets/images/profile/user-3.jpg',
    uname: 'PlayStation 5 DualSense Wireless Controller',
    budget: 120,
    priority: 'rejected',
  },
  {
    id: 4,
    imagePath: 'assets/images/profile/user-4.jpg',
    uname: 'Amazon Basics Mesh, Mid-Back, Swivel Office',
    budget: 160,
    priority: 'confirmed',
  },
];

@Component({
  selector: 'app-tables',
  standalone: true,
  imports: [
    MatTableModule,
    CommonModule,
    MatCardModule,
    MaterialModule,
    MatIconModule,
    MatMenuModule,
    MatButtonModule,
    MatDatepickerModule,
    MatIconModule
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
  templateUrl: './tables.component.html',
})
export class AppTablesComponent implements OnInit {
  // table 1
  displayedColumns1: string[] = ['assigned', 'name', 'priority', 'budget'];
  dataSource1 = PRODUCT_DATA;
  displayedUserColumns: string[] = [
    'id',
    'document_number',
    'names',
    'last_names',
    'email',
    'userName',
    'actions',
  ];
  usersList: User[] = [];
  myCUser: User | null;
  readonly dialog = inject(MatDialog);

  constructor(private userService: UsersListService) {}

  ngOnInit(): void {
    this.userService.getUsersList().subscribe((usersList) => {
      this.usersList = usersList; // Asigna los datos directamente
      console.log(this.usersList);
    });
  }

  openDialog() {
    this.dialog.open(CreateUserDialog, {
      width: '600px',
    });
  }

  hola() {
    let myUser = {
      user_id: 0,
      names: 'Jenny Paola',
      last_names: 'Rodriguez Rodriugez',
      email: 'jenrdos@correo.com',
      document_number: '11188845474',
      username: 'jenrod',
      password: 'pruebaTest123',
      born_date: '1999-04-17T04:49:49.959',
      userType_id: 1,
      userTypes: {
        userType_id: 0,
        userType_name: 'string',
        isDeleted: false,
      },
      document_id: 1,
      documents: {
        document_id: 0,
        document_name: 'string',
        isDeleted: false,
      },
      date: '2024-10-21T07:49:49.959',
      modified: '2024-10-21T07:49:49.959',
      modifiedBy: 'front',
      isDeleted: false,
    };
    this.myCUser = myUser;
    console.log(this.myCUser);

    this.userService.setUser(myUser);
  }
}

@Component({
  selector: 'create-user',
  templateUrl: 'create-user.html',
  standalone: true,
  imports: [
    MatDialogTitle,
    MatDialogContent,
    MatDialogActions,
    MatDialogClose,
    MatButtonModule,
    MatError,
    FormsModule,
    ReactiveFormsModule,
    MatLabel,
    MatFormFieldModule,
    MatInputModule,
    MatDatepickerModule,
    MatIconModule
  ],
  providers: [provideNativeDateAdapter()],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class CreateUserDialog {

  hide = true;

  constructor(private userService: UsersListService) {}


  profileForm = new FormGroup({
    firstName: new FormControl('', [Validators.required]),
    lastName: new FormControl('', [Validators.required]),
    documentNumber: new FormControl('', [Validators.required]),
    userEmail: new FormControl('', [Validators.required, Validators.email]),
    birthDate: new FormControl('', [Validators.required]),
    userName: new FormControl('', [Validators.required]),
    tempPwd: new FormControl('', [Validators.required]),
  });

  submitUserCreation() {
    if (this.profileForm.valid) {
      let newUser: User = {
        user_id: 0,
        names: this.profileForm.controls['firstName'].value ?? '',
        last_names: this.profileForm.controls['lastName'].value ?? '',
        email: this.profileForm.controls['userEmail'].value ?? '',
        document_number:
          this.profileForm.controls['documentNumber'].value ?? '',
        username: this.profileForm.controls['userName'].value ?? '',
        password: this.profileForm.controls['tempPwd'].value ?? '',
        born_date: this.profileForm.controls['birthDate'].value ?? '',
        userType_id: 1,
        userTypes: {
          userType_id: 0,
          userType_name: 'string',
          isDeleted: false,
        },
        document_id: 1,
        documents: {
          document_id: 0,
          document_name: 'string',
          isDeleted: false,
        },
        date: new Date().toISOString(),
        modified: new Date().toISOString(),
        modifiedBy: 'front',
        isDeleted: false,
      };
      console.log(this.profileForm.value);
      this.userService.setUser(newUser);
    } else {
      console.error('Formulario no es válido');
    }
  }

  updateUser(userId: number) {

  }

  deleteUser(userId: number) {

  }

  hola() {
    console.log('hola');
  }
}
