    MaterialModule,
    MatIconModule,
    MatMenuModule,
    MatButtonModule,
    MatDatepickerModule,
    MatIconModule,
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
  templateUrl: './tables.component.html',
})
export class AppTablesComponent implements OnInit {
  displayedUserColumns: string[] = [
    'id',
    'document_number',
    'names',
    'last_names',
    'bornDate',
    'email',
    'username',
    'actions',
  ];
  usersList: User[] = [];
  myCUser: User | null;
  readonly dialog = inject(MatDialog);

  constructor(
    private userService: UsersListService,
    private cdr: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.userService.getUsersList().subscribe((usersList) => {
      this.usersList = usersList;
      console.log(this.usersList);
      this.cdr.detectChanges();
    });
  }

  openDialog() {
    this.dialog.open(CreateUserDialog, {
      width: '600px',
    });
  }

  updateUser(user: User) {
    console.log(user);

    this.dialog.open(EditDialog, {
      width: '600px',
      data: user,
    });
  }

  deleteUser(userId: number) {
    console.log(userId);
    Swal.fire({
      title: '¿Estás seguro de eliminar este usuario?',
      text: 'Esta acción no se puede deshacer',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Sí, eliminar',
    }).then((result) => {
      if (result.isConfirmed) {
        this.userService.deleteUser(userId).subscribe({
          next: (rta) => {
            Swal.fire({
              title: '¡Hecho!',
              text: 'El usuario ha sido eliminado.',
              icon: 'success',
            }).then(() => {
              this.reloadUsersList();
            });
          },
          error: (error) => {
            console.error('Error de validación:', error.error.errors);

            Swal.fire({
              title: 'Error',
              text: 'Ocurrió un error al eliminar el usuario.',
              icon: 'error',
            });

            if (error.error.errors) {
              for (const key in error.error.errors) {
                if (error.error.errors.hasOwnProperty(key)) {
                  console.log(
                    `Error en el campo ${key}:`,
                    error.error.errors[key]
                  );
                }
              }
            }
          },
        });
      }
    });
  }

  reloadUsersList() {
    this.userService.getUsersList().subscribe((usersList) => {
      this.usersList = usersList;
      console.log('Lista actualizada de usuarios:', this.usersList);
    });
  }
}

